mapper.select:
SELECT uam.user_award_id, uam.create_time, uam.used_status, uam.point, buyer.user_id, buyer.name, s.shop_id, s.shop_name, a.award_id, a.award_name, operator.operator_id as operator_id, operator.name as operator_name FROM tb_user_award_map uam, tb_shop s, tb_person_info buyer, tb_award a, tb_person_info operator
uam.user_id = buyer.user_id and uam.shop_id = s.shop_id and uam.award_id = a.award_id and uam.operator_id = operator.user_id
and uam.user_id = #{userAwardCondition.user.userId}
and uam.shop_id = #{userAwardCondition.shop.shopId}
and buyer.name like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and uam.used_status = #{userAwardCondition.usedStatus}
ORDER BY uam.create_time DESC LIMIT #{rowIndex},#{pageSize};

SELECT COUNT(1) FROM tb_user_award_map uam, tb_award a, tb_shop s, tb_person_info buyer
uam.user_id = buyer.user_id and uam.shop_id = s.shop_id and uam.award_id = a.award_id
and uam.user_id = #{userAwardCondition.user.userId}
and uam.shop_id = #{userAwardCondition.shop.shopId}
and buyer.name like '%${userAwardCondition.user.name}%'
and a.award_name like '%${userAwardCondition.award.awardName}%'
and uam.used_status = #{userAwardCondition.usedStatus}

mapper.update:
        UPDATE
        tb_user_award_map

             operator_id = #{operator.userId},
            used_status = #{usedStatus}

        WHERE
        user_award_id = #{userAwardId}
		AND
		user_id = #{user.userId}
